update phieunhap
set tienNhap = (select SUM(soluong*gianhap)
				from ChiTietPN,phieunhap
				where ChiTietPN.maPN= phieunhap.maPN)

select CTphieunhap.maPN,sum(soluong)*sum(gianhap) as tongtien
from phieunhap,CTphieunhap
group by CTphieunhap.maPN


update phieunhap
set tiennhap = (select sum(soluong*gianhap)
				from CTphieunhap
				where phieunhap.maPN = CTphieunhap.maPN
				group by maPN)

