create trigger tt on CTphieunhap
after
insert,
update,
delete
as
begin
update phieunhap
set tiennhap = (select sum(soluong*gianhap)
				from CTphieunhap
				where phieunhap.maPN = CTphieunhap.maPN
				group by maPN)

end